/**
 * Technique copied from https://github.com/d3/d3-scale-chromatic/blob/90e7c02c6c676d1298dc2cd3d4bda29855fd7232/src/sequential-multi/viridis.js
 * Ramp descriptions taken from color_ramps.go
 * Ramp JSON generated using rampgenerator.com
 * Ramp string generated from the ramp JSON using the following:
 * rampStr = rampJson.ramp.reduce((acc, val) => acc += val.color.slice(1), '')
 */

import colors from "d3-scale-chromatic/src/colors";

function ramp(range) {
  var n = range.length;
  return function (t) {
    return range[Math.max(0, Math.min(n - 1, Math.floor(t * n)))];
  };
}

export const RedYellowGreen = ramp(
  colors(
    ""
  )
);

export const BrownYellowBlue = ramp(
  colors(
    ""
  )
);
